import React, { useState } from 'react'
import axios from 'axios';
import Cookie from 'universal-cookie';
import Swal from 'sweetalert2'
import"./MonyTransfer.css"
// import 'bootstrap/dist/css/bootstrap.min.css';



const MonyTransfer = () => {
  const cookie = new Cookie()
  const token = cookie.get('bearer')

    const[accountNumberFrom,setaccountNumberFrom]=useState('');
    const[accountNumberTo,setaccountNumberTo]=useState('');
    const[typemony,settypemony]=useState('USD');
    const[description,setdescription]=useState('');
    const[typeto,settypeto]=useState('USD');
    const[amount,setamount]=useState('');
    const [loading , setLoading] = useState(false)


    function MonyFrom(e){
        settypemony(e.currentTarget.value)
      }
    function MonyTO(e){
        settypeto(e.currentTarget.value)
      }



    async function Submit(e) { 
    let Flage=true
    e.preventDefault();

    setLoading(true);
    if (accountNumberFrom===''||accountNumberTo===''||amount===''||description==='' ) {
        Flage=false
    }
    else{
        Flage=true;
    }
    if(Flage){

try{
  let res = await axios.post('http://127.0.0.1:8000/api/dotransaction',
  {
   accountNumberFrom:accountNumberFrom,
   accountNumberTo:accountNumberTo,
   typemony:typemony,
   typeto:typeto,
   amount:amount,
   description:description

  },{
   headers:{
     Authorization : 'Bearer ' + token
   }
 }
  )
  .then((response)=>console.log(response));


  setLoading(false)
  await Swal.fire({
    position: 'center',
    icon: 'success',
    title: 'تم إجراء المناقلة  بنجاح',
    showConfirmButton: false,
    timer: 1500
  })
} catch(err){
  setLoading(false)
  Swal.fire({
    icon: 'error',
    title: 'حدث خطأ ما',
    text: 'تعذر إضافة الموظف',
  })
  console.log(err)
}
   
    }
    
}

  return (
    <div className="container " >

    {/* <div   className='col'
     style={{
      backgroundImage:`url(${require('./transfermony.jpg')})`,
      width:'300px',
      height:'300px',
      backgroundSize:'cover',
      backgroundRepeat:'no-repeat'
    
    }}>

    </div > */}
    <form className=" bg-body-secondary p-4 w-50 border border-danger border-2 " onSubmit={Submit} >
    <h3 className="text-center text-danger  ">تحويل المال بين الحسابات  </h3>
<div className=" row"  style={{textAlign:'right'}}>
<div className=" col-lg-6 col-md-12 col-sm-12" style={{textAlign:'right'}}>
<label htmlFor="exampleInputID23" className="form-label text-danger">نوع المال المرسل</label>
<select id='exampleInputID23' className="form-control" onChange={MonyFrom} style={{textAlign:'right'}}>
      <option value='USD'>
      USD
        </option>
      <option value='TRY'>
        TRY
        </option>
       <option value='EUR'>
       EUR
    </option>
    </select>
</div>

<div className=" col-lg-6 col-md-12 col-sm-12 ">
<label htmlFor="exampleInputaccf" className="form-label text-danger" >رقم حساب المرسل </label>
<input type="number" className="form-control "
 id="exampleInputaccf" aria-describedby="emailHelp" 
 name="accountNumberFrom" required
 value={accountNumberFrom}
 onChange={(e)=>setaccountNumberFrom(e.target.value)}
 style={{textAlign:'right'}} />
 </div>
</div> 

<div className='row' style={{textAlign:'right'}}>
<div className=" col-lg-6 col-md-12 col-sm-12 " style={{textAlign:'right'}}>

<label htmlFor="exampleInputID" className="form-label text-danger">نوع المال المستقبل</label>

<select id='exampleInputID' className="form-control " onChange={MonyTO} style={{textAlign:'right'}}>
     <option value="USD">
     USD
       </option>
     <option value='TRY'>
       TRY
       </option>
      <option value='EUR'>
      EUR
   </option>
   </select>
</div>
<div className=" col-lg-6 col-md-12 col-sm-12 ">
<label htmlFor="exampleInputacct" className="form-label text-danger">رقم حساب المستقبل </label>
<input type="number" className="form-control" id="exampleInputacct" name="accountNumberTo" required
value={accountNumberTo}
onChange={(e)=>setaccountNumberTo(e.target.value)}
style={{textAlign:'right'}}/>
</div>
</div>


<div className="col " style={{textAlign:'right'}}>
<label htmlFor="exampleInputamount" className="form-label text-danger">القيمة</label>
<input type="number" className="form-control" id="exampleInputamount" name="amount" required
value={amount}
onChange={(e)=>setamount(e.target.value)}
style={{textAlign:'right'}}/>
</div>


<div className="col "style={{textAlign:'right'}}>
<label htmlFor="exampleInputdescription" className="form-label text-danger">الوصف </label>
<input type="text" className="form-control " id="exampleInputdescription" name="description" required
value={description}
onChange={(e)=>setdescription(e.target.value)}
style={{textAlign:'right'}}/>
</div>

<button type="submit" className="text-center btn btn-danger"style={{textAlign:'right'}}>تحويل رصيد </button>
</form>


