import { useState,useEffect } from 'react'
import Loading from '../../Loading/Loading'
import Swal from 'sweetalert2'
import axios from 'axios'
import Cookie from 'universal-cookie';

const UserAdd = () => {

  const cookie = new Cookie()
  const token = cookie.get('bearer')

  const [firstname , setFirstname] = useState('')
  const [lastname , setLastname] = useState('')
  const [branch , setBranch] = useState('إدلب')
  const [adrress , setAdrress] = useState('')
  const [id , setID] = useState('')
  const [gender , setGender] = useState('ذكر')
  const [phone , setPhone] = useState('')
  const [email , setEmail] = useState('')
  const [pssword , setPssword] = useState('')
  const [loading , setLoading] = useState(false)
  const[barnchdata,setbarnchdata]=useState([])

console.log(token)

useEffect(()=>{
  axios.get('http://127.0.0.1:8000/api/branches',{
      headers:{
          Authorization: 'Bearer '+token
      }
  }).then(res => setbarnchdata(res.data.data))
  //eslint-disable-next-line react-hooks/exhaustive-deps
},[])
  function firstName(e){
    setFirstname(e.currentTarget.value)
  }
  function lastName(e){
    setLastname(e.currentTarget.value)
  }
  function Branch(e){
    setBranch(e.currentTarget.value)
  }
  function Adrress(e){
    setAdrress(e.currentTarget.value)
  }
  function Id(e){
    setID(e.currentTarget.value)
  }
  function Gender(e){
    setGender(e.currentTarget.value)
  }
  function Phone(e){
    setPhone(e.currentTarget.value)
  }
  function Email(e){
    setEmail(e.currentTarget.value)
  }
  function Password(e){
    setPssword(e.currentTarget.value)
  }
  async function create(e) {
    e.preventDefault();
    if(id.length>=11 & pssword.length>=8){
      setLoading(true)
      try{
        await axios.post('http://127.0.0.1:8000/api/customer/createCustomer',{
          branchaddress:branch,
          firstName:firstname,
          lastName:lastname,
          gender:gender,
          ID_number:id,
          address:adrress,
          email:email,
          phone:phone,
          password:pssword
        },{
          headers:{
            Authorization : 'Bearer ' + token
          }
        })
        setLoading(false)
        await Swal.fire({
          position: 'center',
          icon: 'success',
          title: 'تم إضافة الموظف بنجاح',
          showConfirmButton: false,
          timer: 1500
        })
        window.location.reload();
      }catch(err){
        setLoading(false)
        Swal.fire({
          icon: 'error',
          title: 'حدث خطأ ما',
          text: 'تعذر إضافة الموظف',
        })
        console.log(err)
      }
    }
    else{
      Swal.fire({
        icon: 'error',
        title: 'حدث خطأ ما',
        text: 'تعذر إضافة الموظف تأكد من صحة معلوماتك',
      })
    }
  }
  
  return (
   <>
      {loading && <Loading />}
        <form onSubmit={create}style={{display:'flex',flexWrap:'wrap'}}>
      <div className="form-item" style={{flex:'1 0 140px'}}>
        <input type="text" placeholder="الكنية" value={lastname} onChange={lastName} />
        <input type="text" placeholder="الإسم" value={firstname} onChange={firstName} />
        <select onChange={Branch}>
        {
            barnchdata.map((items)=>(
              // console.log(items.address)
              <option key={items.id} value={items.address} >
                {items.address}
            </option>
            ))
           }
        </select>
      </div>
      <div className="form-item"style={{flex:'1 0 140px'}}>
        <input type="text" placeholder="العنوان" value={adrress} onChange={Adrress} />
        <input type="text" placeholder="الرقم الوطني" value={id} onChange={Id} />
        <select onChange={Gender}>
          <option value="ذكر">
             ذكر  
          </option>
          <option value="أنثى">
          أنثى
          </option>
        </select>
      </div>
      <div className="form-item"style={{flex:'1 0 140px'}}>
        <input type="text" placeholder="رقم الهاتف" value={phone} onChange={Phone} />  
        <input type="email" placeholder="البريد الإلكتروني" value={email} onChange={Email} />

      </div>
      <div className="form-item">
      <input type="password" placeholder="كلمة المرور" value={pssword} onChange={Password} />

      </div>
      <button type='submit' className='login-btn' style={{'transform':'translateX(120px)' ,flex:'1 0 50px'}}>إضافة</button>
    </form>
    </>
  )
}

export default UserAdd
