import axios from 'axios'
import { useEffect, useState } from 'react'
import Loading from '../../Loading/Loading'
import Cookie  from 'universal-cookie'
const EmpShow = () => {
    const [users , setUsers] = useState({})
    const [run , setRun] = useState(0)
    const [loading , setLoading] = useState(false)
    const cookie = new Cookie()
    const token = cookie.get('bearer')
    useEffect(()=>{
        axios.get('http://127.0.0.1:8000/api/user/index',{
            headers:{
                Authorization: 'Bearer '+token
            }
        }).then(res => setUsers(res.data.data))
        //eslint-disable-next-line react-hooks/exhaustive-deps
    },[run])
   let user = Array.from(users) 
   let tr = user.map((item,index)=>{
    return<tr key={index}>
    <td>{item.id}</td>
    <td>{item.firstName}</td>
    <td>{item.lastName}</td>
    <td>{item.email}</td>
    <td>
    <i className="fa-solid fa-trash" style={{'color':'#11101D','padding':'0px 10px','cursor':'pointer'}} onClick={()=>delet(item.id)}></i>
    <i className="fa-solid fa-pen" style={{'color':'#f00','cursor':'pointer'}}></i>
    </td>
    </tr>
   })
   async function delet(id) {
       setLoading(true)
       try{
           await axios.delete(`http://127.0.0.1:8000/api/user/destroy/${id}`,{
               headers:{
                   Authorization : 'Bearer '+token
               }
           })
           setRun( (prev)=> prev + 1)
           setLoading(false)
       }catch(err){
           console.log(err)
           setLoading(false)
       }
   }
  return (
    <div>
        {loading && <Loading />}
        <table dir='rtl'>
            <thead >
                <tr>
                    <th>رقم الموظف</th>
                    <th>اسم الموظف</th>
                    <th>الكنية</th>
                    <th>البريد الإلكتروني</th>
                    <th>المهمة</th>
                </tr>
            </thead>
            <tbody>
                {tr}
            </tbody>
        </table>
    </div>
  )
}

export default EmpShow
