import React, { useState ,useEffect} from 'react'
import axios from 'axios';
import Cookie from 'universal-cookie';
import Swal from 'sweetalert2'
import { Loading } from '../../import';

const UserAdd = () => {


console.log(token)
const cookie = new Cookie()
const token = cookie.get('bearer')

const [firstName , setfirstName] = useState('')
const [lastName , setlastName] = useState('')
const [branch , setBranch] = useState('edlib')
const [address , setaddress] = useState('')
const [ID_number , setID_number] = useState('')
const [gender , setgender] = useState('ذكر')
const [phone , setphone] = useState('')
const [email , setemail] = useState('')
const [password , setpassword] = useState('')
const [loading , setLoading] = useState(false)
const[barnchdata,setbarnchdata]=useState([])


function Branch(e){
  setBranch(e.currentTarget.value)
}
function Gender(e){
  setgender(e.currentTarget.value)
}

  useEffect(()=>{
      axios.get('http://127.0.0.1:8000/api/branches',{
          headers:{
              Authorization: 'Bearer '+token
          }
      }).then(res => setbarnchdata(res.data.data))
      //eslint-disable-next-line react-hooks/exhaustive-deps
  },[])




  async function Submit(e) { 
      let Flage=true
      e.preventDefault();
  
      setLoading(true);
      if (firstName===''||lastName===''||branch===''||ID_number<=10 ) {
          Flage=false
      }
      else{
          Flage=true;
      }
      if(Flage){
  
  try{
    let res = await axios.post('http://127.0.0.1:8000/api/customer/createCustomer',
    {
      branchaddress:branch,
      firstName:firstName,
      lastName:lastName,
      gender:gender,
      ID_number:ID_number,
      address:address,
      email:email,
      phone:phone,
      password:password
  
    },{
     headers:{
       Authorization : 'Bearer ' + token
     }
   }
    )
    .then((response)=>console.log(response));
  
  
    setLoading(false)
    await Swal.fire({
      position: 'center',
      icon: 'success',
      title: 'تم إضافة الموظف  بنجاح',
      showConfirmButton: false,
      timer: 1500
    })
  } catch(err){
    setLoading(false)
    Swal.fire({
      icon: 'error',
      title: 'حدث خطأ ما',
      text: 'تعذر إضافة الموظف',
    })
    console.log(err)
  }
     
      }
      
  }
  
  return (
    <div>

    {loading && <Loading />}
            <form onSubmit={Submit}>
          <div className="form-item">
            <input type="text" placeholder="الكنية" value={lastName} onChange={(e)=>setlastName(e.target.value)}  required/>
            <input type="text" placeholder="الإسم" value={firstName} onChange={(e)=>setfirstName(e.target.value)} required/>
            <select onChange={Branch}>
    
               {
                barnchdata.map((items)=>(
                  // console.log(items.address)
                  <option key={items.id} value={items.address} >
                    {items.address}
                </option>
                ))
               }
      
              {/* <option value='أريحا'>
                أريحا
                </option>
               <option value='جسر الشغور'>
                جسر الشغور
                </option>
              <option value='سرمدا'> 
                سرمدا
                </option> */}
            </select>
          </div>
          <div className="form-item">
            <input type="text" placeholder="العنوان" value={address} onChange={(e)=>setaddress(e.target.value)} required/>
            <input type="text" placeholder="الرقم الوطني" value={ID_number} onChange={(e)=>setID_number(e.target.value)} required/>
    
            <select onChange={Gender}>
              <option value="ذكر">
                 ذكر  
              </option>
              <option value="أنثى">
              أنثى
              </option>
    
            </select>
          </div>
          <div className="form-item">
            <input type="text" placeholder="رقم الهاتف" value={phone} onChange={(e)=>setphone(e.target.value)} required/>  
            <input type="email" placeholder="البريد الإلكتروني" value={email} onChange={(e)=>setemail(e.target.value)} required />

          </div>
          <div className="form-item">
          <input type="password" placeholder="كلمة المرور" value={password} onChange={(e)=>setpassword(e.target.value)} required/>
          </div>
          <button type='submit' className='login-btn' style={{'transform':'translateX(120px)'}}>إرسال</button>
        </form>
        </div>
  )
}

export default UserAdd
